import { useState } from 'react';
import apple from './assets/apple.jpg'
import { StripeContainer } from './components/PaymentContainer';


function App() {
	const [showItem, setShowItem] = useState(false)
	return (
		<div>
			<h1>
				Fruit Store
			</h1>
			{showItem ? (
				<StripeContainer />
			) : (
				<>
					<h3>$10.00</h3>
					<img src={apple} alt='apple' />
					<button onClick={() => setShowItem(true)}>APPLE</button>
				</>
			)}

		</div>

	);
}

export default App;
